package cuarto;

/**
 * @author usuario
 *
 */
public class cuarto {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/* Hacer un programa que imprima la suma de los 100 primeros números.
		 */
		int contador = 0;
		int a = 0;
		while (contador <= 100) {
			a= a + contador;
			contador ++;
		} 
		System.out.println(a);	
	}

}
