public class segundo {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub 
		/*Hacer un pseudocódigo que imprima los números del 100 al 0, en orden decreciente.
		 */
		
		
		int contador = 100;
		while (contador >= 0) {
			System.out.println(contador);
			contador --;
			
		}
	}

}
